import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertPlcConfigDomainSchema, configFileSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {

  // Get all PLC configurations
  app.get("/api/plc-configurations", async (req, res) => {
    try {
      const configurations = await storage.getAllPlcConfigurations();
      res.json(configurations);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch configurations" });
    }
  });

  // Get a specific PLC configuration
  app.get("/api/plc-configurations/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const configuration = await storage.getPlcConfiguration(id);

      if (!configuration) {
        return res.status(404).json({ message: "Configuration not found" });
      }

      res.json(configuration);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch configuration" });
    }
  });

  // Create a new PLC configuration
  app.post("/api/plc-configurations", async (req, res) => {
    try {
      const validatedData = insertPlcConfigDomainSchema.parse(req.body);

      // config_data validation is already included in insertPlcConfigDomainSchema

      const configuration = await storage.createPlcConfiguration(validatedData);
      res.status(201).json(configuration);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          message: "Validation error", 
          errors: error.errors 
        });
      }
      res.status(500).json({ message: "Failed to create configuration" });
    }
  });

  // Update a PLC configuration
  app.put("/api/plc-configurations/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const validatedData = insertPlcConfigDomainSchema.partial().parse(req.body);

      // config_data validation is already included in insertPlcConfigDomainSchema

      const configuration = await storage.updatePlcConfiguration(id, validatedData);

      if (!configuration) {
        return res.status(404).json({ message: "Configuration not found" });
      }

      res.json(configuration);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          message: "Validation error", 
          errors: error.errors 
        });
      }
      res.status(500).json({ message: "Failed to update configuration" });
    }
  });

  // Delete a PLC configuration
  app.delete("/api/plc-configurations/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const deleted = await storage.deletePlcConfiguration(id);

      if (!deleted) {
        return res.status(404).json({ message: "Configuration not found" });
      }

      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete configuration" });
    }
  });

  // Validate configuration data
  app.post("/api/plc-configurations/validate", async (req, res) => {
    try {
      const validatedData = configFileSchema.parse(req.body);
      res.json({ valid: true, data: validatedData });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          valid: false,
          message: "Validation error", 
          errors: error.errors 
        });
      }
      res.status(500).json({ valid: false, message: "Validation failed" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type User, type InsertUser, type PlcConfiguration, type InsertPlcConfigurationDomain } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  getPlcConfiguration(id: string): Promise<PlcConfiguration | undefined>;
  getAllPlcConfigurations(): Promise<PlcConfiguration[]>;
  createPlcConfiguration(config: InsertPlcConfigurationDomain): Promise<PlcConfiguration>;
  updatePlcConfiguration(id: string, config: Partial<InsertPlcConfigurationDomain>): Promise<PlcConfiguration | undefined>;
  deletePlcConfiguration(id: string): Promise<boolean>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private plcConfigurations: Map<string, PlcConfiguration>;

  constructor() {
    this.users = new Map();
    this.plcConfigurations = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  async getPlcConfiguration(id: string): Promise<PlcConfiguration | undefined> {
    return this.plcConfigurations.get(id);
  }

  async getAllPlcConfigurations(): Promise<PlcConfiguration[]> {
    return Array.from(this.plcConfigurations.values());
  }

  async createPlcConfiguration(insertConfig: InsertPlcConfigurationDomain): Promise<PlcConfiguration> {
    const id = randomUUID();
    const config: PlcConfiguration = {
      ...insertConfig,
      id,
      created_at: new Date().toISOString(),
      description: insertConfig.description || null,
    };
    this.plcConfigurations.set(id, config);
    return config;
  }

  async updatePlcConfiguration(id: string, updateData: Partial<InsertPlcConfigurationDomain>): Promise<PlcConfiguration | undefined> {
    const existingConfig = this.plcConfigurations.get(id);
    if (!existingConfig) {
      return undefined;
    }

    const updatedConfig: PlcConfiguration = {
      ...existingConfig,
      ...updateData,
    };
    this.plcConfigurations.set(id, updatedConfig);
    return updatedConfig;
  }

  async deletePlcConfiguration(id: string): Promise<boolean> {
    return this.plcConfigurations.delete(id);
  }
}

import { SqliteStorage } from "./sqlite-storage";

export const storage = new SqliteStorage();

